import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Users, Wrench, Package } from 'lucide-react';

interface RoleOption {
  id: string;
  title: string;
  description: string;
  icon: React.ReactNode;
  color: string;
  path: string;
}

const roleOptions: RoleOption[] = [
  {
    id: 'service-advisor',
    title: 'Service Advisor',
    description: 'Customer communication, case management, and repair coordination',
    icon: <Users size={32} />,
    color: 'bg-blue-500',
    path: '/metrics/service-advisor'
  },
  {
    id: 'parts-staff',
    title: 'Parts Staff',
    description: 'Parts ordering, inventory management, and procurement',
    icon: <Package size={32} />,
    color: 'bg-green-500',
    path: '/metrics/parts-staff'
  },
  {
    id: 'technician',
    title: 'Foremen/Technicians',
    description: 'Repair execution, diagnostics, and technical documentation',
    icon: <Wrench size={32} />,
    color: 'bg-orange-500',
    path: '/metrics/technician'
  }
];

export default function MetricsRoleSelection() {
  const navigate = useNavigate();

  const handleRoleSelect = (path: string) => {
    navigate(path);
  };

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold text-slate-900 mb-4">
          Dealer Scorecard Metrics
        </h1>
        <p className="text-xl text-slate-600 mb-6">
          Choose your role to see how your actions impact WKI dealer performance metrics
        </p>
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 inline-block">
          <p className="text-yellow-800 font-semibold">
            Understanding your impact drives better performance for everyone
          </p>
        </div>
      </div>

      <div className="grid md:grid-cols-3 gap-8">
        {roleOptions.map((role) => (
          <button
            key={role.id}
            onClick={() => handleRoleSelect(role.path)}
            className="group bg-white rounded-2xl shadow-lg p-8 text-center hover:shadow-xl transition-all duration-300 hover:scale-105 border border-slate-200"
          >
            <div className={`${role.color} w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-6 text-white group-hover:scale-110 transition-transform duration-300`}>
              {role.icon}
            </div>
            
            <h3 className="text-2xl font-bold text-slate-900 mb-4 group-hover:text-blue-600 transition-colors">
              {role.title}
            </h3>
            
            <p className="text-slate-600 leading-relaxed mb-6">
              {role.description}
            </p>
            
            <div className="text-blue-600 font-semibold group-hover:text-blue-700">
              View My Impact â†’
            </div>
          </button>
        ))}
      </div>

      <div className="mt-12 bg-slate-50 rounded-lg p-6 text-center">
        <h3 className="text-lg font-semibold text-slate-900 mb-2">
          Key Performance Areas
        </h3>
        <div className="flex flex-wrap justify-center gap-4 text-sm text-slate-600">
          <span className="bg-white px-3 py-1 rounded-full">Triage Time</span>
          <span className="bg-white px-3 py-1 rounded-full">Dwell Time</span>
          <span className="bg-white px-3 py-1 rounded-full">Customer Satisfaction</span>
          <span className="bg-white px-3 py-1 rounded-full">First-Time Fix Rate</span>
          <span className="bg-white px-3 py-1 rounded-full">Parts Availability</span>
          <span className="bg-white px-3 py-1 rounded-full">Communication Quality</span>
        </div>
      </div>
    </div>
  );
}
