import React, { useEffect, useState } from 'react';

interface SplashScreenProps {
  onFinish: () => void;
}

export default function SplashScreen({ onFinish }: SplashScreenProps) {
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(false);
      // Additional delay for fade out animation
      setTimeout(() => {
        onFinish();
      }, 300);
    }, 2500); // 2.5 seconds

    return () => clearTimeout(timer);
  }, [onFinish]);

  return (
    <div
      className={`fixed inset-0 z-50 flex items-center justify-center bg-cover bg-center bg-no-repeat transition-opacity duration-300 ${
        isVisible ? 'opacity-100' : 'opacity-0'
      }`}
      style={{
        backgroundImage: 'url(/WKIsplash.png)'
      }}
    >
      {/* Loading indicator */}
      <div className="absolute bottom-20 left-1/2 transform -translate-x-1/2 animate-pulse">
        <div className="flex space-x-2">
          <div className="w-3 h-3 bg-white/70 rounded-full animate-bounce"></div>
          <div className="w-3 h-3 bg-white/70 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
          <div className="w-3 h-3 bg-white/70 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
        </div>
      </div>
    </div>
  );
}
