import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navigation from "./components/Navigation";
import ProcessWorkflowLayout from "./components/ProcessWorkflowLayout.tsx";
import MetricsRoleSelection from "./components/MetricsRoleSelection";
import ServiceAdvisorMetrics from "./components/ServiceAdvisorMetrics";
import PartsStaffMetrics from "./components/PartsStaffMetrics";
import TechnicianMetrics from "./components/TechnicianMetrics";
import "./index.css";

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-slate-100">
        <Navigation />
        <div className="container mx-auto">
          <Routes>
            <Route path="/" element={
              <div className="flex flex-col items-center justify-center py-8">
                <ProcessWorkflowLayout />
              </div>
            } />
            <Route path="/metrics" element={<MetricsRoleSelection />} />
            <Route path="/metrics/service-advisor" element={<ServiceAdvisorMetrics />} />
            <Route path="/metrics/parts-staff" element={<PartsStaffMetrics />} />
            <Route path="/metrics/technician" element={<TechnicianMetrics />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
