import React, { useState } from "react";

interface Step {
  title: string;
  id: string;
  roles: string[];
  isQAB?: boolean;
  actionButton?: string;
}

interface RoleFilter {
  id: string;
  name: string;
}

const steps: Step[] = [
  { title: "Case Created\n& Truck Arrives Onsite", id: "case-created", roles: ["service-advisor"], isQAB: true, actionButton: "CHECK-IN" },
  { title: "Create RO\nfrom Decisiv Case", id: "create-ro", roles: ["service-advisor"] },
  { title: "Update Case\nwith Lines & Parts", id: "update-case", roles: ["service-advisor", "parts-staff"] },
  { title: "Send Initial\nEstimate to Customer", id: "send-estimate", roles: ["service-advisor"] },
  { title: "Customer Approves\nEstimate", id: "approve-estimate", roles: ["service-advisor"], isQAB: true, actionButton: "REQUEST APPROVAL" },
  { title: "Parts Picked or\nOrdered for RO", id: "parts-picked", roles: ["parts-staff"] },
  { title: "Repair in\nProcess", id: "repair-process", roles: ["technician"] },
  { title: "Technician Story\nWrite Up", id: "tech-writeup", roles: ["technician"] },
  { title: "Service Advisor\nAdmin Work", id: "admin-work", roles: ["service-advisor"], isQAB: true },
  { title: "Notify Customer\nRepair is Complete", id: "notify-customer", roles: ["service-advisor"] },
  { title: "Invoice\nCustomer", id: "invoice-customer", roles: ["service-advisor"] },
  { title: "Truck Departs\nService Location", id: "truck-departs", roles: ["service-advisor"], actionButton: "ASSET READY" },
];

const roleFilters: RoleFilter[] = [
  { id: 'all', name: 'All Roles' },
  { id: 'service-advisor', name: 'Service Advisors' },
  { id: 'parts-staff', name: 'Parts Staff' },
  { id: 'technician', name: 'Foremen/Technicians' }
];

export default function ProcessWorkflowLayout() {
  const [activeFilter, setActiveFilter] = useState<string>('all');
  const [hoveredStep, setHoveredStep] = useState<string | null>(null);

  const getStepOpacity = (step: Step): string => {
    if (activeFilter === 'all') return 'opacity-100';
    return step.roles.includes(activeFilter) ? 'opacity-100' : 'opacity-30';
  };

  const handleStepClick = (stepId: string) => {
    console.log(`Clicked step: ${stepId}`);
    // Future: Navigate to step details or trigger action
  };

  return (
    <div className="w-full max-w-6xl bg-white rounded-2xl shadow-xl p-8 flex flex-col gap-8">
      {/* Header */}
      <div className="flex justify-between items-center mb-2">
        <div>
          <h1 className="text-3xl font-bold text-slate-900">Service Management Process</h1>
          <p className="text-blue-700 font-semibold">Start Every Repair in Service Management</p>
        </div>
        <div className="flex items-center gap-2">
          <div className="h-12 w-12 rounded-lg bg-blue-600 flex items-center justify-center shadow">
            <span className="text-white font-bold text-lg">K</span>
          </div>
          <span className="text-xs text-slate-700 font-bold">KENWORTH<br />TRUCKTECH+<br />Service Management</span>
        </div>
      </div>

      {/* Filter by Role - moved below header */}
      <div className="flex flex-wrap gap-2 mb-6 justify-center">
        <span className="text-slate-700 font-semibold mr-2 self-center">Filter by Role:</span>
        {roleFilters.map(role => (
          <button
            key={role.id}
            onClick={() => setActiveFilter(role.id)}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              activeFilter === role.id
                ? 'bg-blue-600 text-white shadow-md'
                : 'bg-slate-200 text-slate-700 hover:bg-slate-300'
            }`}
          >
            {role.name}
          </button>
        ))}
      </div>

      {/* Timeline Workflow - boxes above the line */}
      <div className="relative flex flex-col items-center">
        {/* Step Boxes above the line */}
        <div className="flex w-full justify-between items-end z-10 mb-6">
          {steps.map((step, idx) => (
            <div 
              key={step.id} 
              className={`flex flex-col items-center w-32 transition-all duration-300 ${getStepOpacity(step)}`}
              onMouseEnter={() => setHoveredStep(step.id)}
              onMouseLeave={() => setHoveredStep(null)}
            >
              {/* Step Box */}
              <button
                onClick={() => handleStepClick(step.id)}
                className={`bg-blue-100 border border-blue-400 rounded-lg px-3 py-2 text-center text-xs font-semibold whitespace-pre-line shadow-md mb-2 transition-all hover:shadow-lg hover:scale-105 ${
                  hoveredStep === step.id ? 'bg-blue-200 border-blue-500' : ''
                }`}
              >
                {step.title}
              </button>
              
              {/* QAB marker */}
              {step.isQAB && (
                <div className="mt-2 flex flex-col items-center">
                  <div className="bg-yellow-400 rounded-full w-10 h-10 flex items-center justify-center font-bold text-black text-lg border-2 border-yellow-600 shadow">
                    QAB
                  </div>
                  <div className="h-4" />
                </div>
              )}
              
              {/* Action Button */}
              {step.actionButton && (
                <button 
                  onClick={() => console.log(`Action: ${step.actionButton} for ${step.id}`)}
                  className="bg-blue-600 text-white px-3 py-1 rounded-lg font-bold text-xs shadow mt-2 hover:bg-blue-700 transition-colors"
                >
                  {step.actionButton}
                </button>
              )}
            </div>
          ))}
        </div>
        
        {/* Timeline Line */}
        <div className="w-full h-1 bg-blue-400 z-0 relative">
          <div className="absolute left-0 top-0 h-full bg-blue-600 transition-all duration-500" style={{width: '30%'}} />
        </div>
        
        {/* Timeline Labels */}
        <div className="flex w-full justify-between mt-2 text-slate-700 font-semibold text-sm">
          <span>Triage Time</span>
          <span>Dwell Time</span>
        </div>
      </div>

      {/* Bottom Section */}
      <div className="flex flex-col md:flex-row gap-6 mt-8">
        <div className="bg-yellow-400 rounded-lg p-6 flex-1 flex flex-col justify-center items-center shadow">
          <h3 className="text-xl font-bold text-blue-900 mb-2">Kenworth Goals</h3>
          <div className="text-blue-900 font-semibold text-lg">Triage: 4 Hours or less</div>
          <div className="text-blue-900 font-semibold text-lg">Dwell: 3 Days or less</div>
        </div>
        <div className="flex-1">
          <h3 className="text-xl font-bold mb-2 text-slate-900">Remember Every Case Needs:</h3>
          <ul className="list-disc list-inside text-slate-800 text-sm grid grid-cols-1 md:grid-cols-2 gap-x-4 gap-y-1">
            <li>Primary Email Contact</li>
            <li>Follow Up Time</li>
            <li>Case-Level Complaint</li>
            <li>Accurate Estimate Approvals</li>
            <li>Quick Action Button Usage</li>
            <li>Every Case Updated Twice Daily</li>
            <li>ETR- Estimated Time to Repair Populated</li>
            <li>Repair Order # from DBS</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
