import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { BarChart3, Workflow } from 'lucide-react';

export default function Navigation() {
  const location = useLocation();
  const isWorkflow = location.pathname === '/';
  const isMetrics = location.pathname.startsWith('/metrics');

  return (
    <nav className="bg-white shadow-sm border-b border-slate-200 mb-8">
      <div className="max-w-6xl mx-auto px-4 py-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-8">
            <div className="flex items-center space-x-2">
              <img 
                src="https://www.kenworth.com/media/w4jnzm4t/kenworth_logo-header-new-012023.png" 
                alt="Kenworth Logo" 
                className="h-8 w-auto" 
              />
              <span className="text-lg font-bold text-slate-900">WKI Service Management</span>
            </div>
            
            <div className="flex space-x-1">
              <Link
                to="/"
                className={`flex items-center space-x-2 px-4 py-2 rounded-lg font-medium transition-all ${
                  isWorkflow 
                    ? 'bg-blue-600 text-white shadow-md' 
                    : 'text-slate-600 hover:text-slate-900 hover:bg-slate-100'
                }`}
              >
                <Workflow size={18} />
                <span>Workflow</span>
              </Link>
              
              <Link
                to="/metrics"
                className={`flex items-center space-x-2 px-4 py-2 rounded-lg font-medium transition-all ${
                  isMetrics 
                    ? 'bg-blue-600 text-white shadow-md' 
                    : 'text-slate-600 hover:text-slate-900 hover:bg-slate-100'
                }`}
              >
                <BarChart3 size={18} />
                <span>Metrics</span>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
}
